class NewPeak extends Component{
    constructor(props){
      super(props)
      this.state = { peak_name:'', peak_elv:null, bag_date:''}
      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
    }
    
    // Form submitting logic, prevent default page refresh 
    handleSubmit(event){
      const { peak_name, peak_elv, bag_date} = this.state
      event.preventDefault()
      alert(`
        ____Your Details____\n
        peak_name : ${peak_name}
        Name : ${peak_elv}
        Age : ${bag_date}
      `)
    }
    
    // Method causes to store all the values of the 
    // input field in react state single method handle 
    // input changes of all the input field using ES6 
    // javascript feature computed property names
    handleChange(event){
      this.setState({
        // Computed property names
        // keys of the objects are computed dynamically
        [event.target.name] : event.target.value
      })
    }
    
    // Return a controlled form i.e. values of the 
    // input field not stored in DOM values are exist 
    // in react component itself as state
    render(){
      return(
        <form onSubmit={this.handleSubmit}>
          <div>
            <label htmlFor='peak_name'>peak_name</label>
            <input 
              name='peak_name'
              placeholder='peak_name' 
              value = {this.state.peak_name}
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label htmlFor='name'>Name</label>
            <input
              name='name' 
              placeholder='Name'
              value={this.state.peak_elv}
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label htmlFor='age'>Age</label>
            <input
              name='age' 
              placeholder='Age'
              value={this.state.bag_date}
              onChange={this.handleChange}
            />
          </div>
            <button>Create Account</button>
        </form>
      )
    }
  }

export default NewPeak;
