import React, {Fragment, useState} from "react";

const EditElv = ({peak}) => {
  
    const [peak_elv, setElv] = useState("peak.peak_elv");

    const onSubmitForm = async(e) => {
        e.preventDefault();

        try {
            const body = {peak_elv};
            const response = await fetch(`http://localhost:5000/peaks/${peak.peak_id}`,{
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body)
            });
            window.location = "/";
        } catch (err) {
            console.error(err.message);
        };
    };

    return (
        <Fragment>
            <button type="button" class="btn btn-info" data-toggle="modal" data-target={`#id${peak.peak_id}`}>Edit</button>
            <div class="modal" id={`id${peak.peak_id}`} onClick={() => setElv(peak.elv)}>
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h4 class="modal-title">Edit peak</h4>
                            <button type="button" class="close" data-dismiss="modal" onClick={() => setElv(peak.elv)}>&times;</button>
                        </div>

                        <div className="modal-body">
                            <input type="text" className="form-control" value={peak_elv} onChange={e => setElv(e.target.value)}/>
                        </div>

                        <div class="modal-footer">
                            <button type="button" class="btn btn-warning" data-dismiss="modal" onClick={e => onSubmitForm(e)}>Confirm</button>
                            <button type="button" class="btn btn-danger"  data-dismiss="modal" onClick={() => setElv(peak.elv)}>Exit</button>
                        </div>
                    </div>
                </div>
            </div>
        </Fragment>
    );
};

export default EditElv;
