        } catch (err) {
            console.error(err.message)
        }
    }

    //list function
    const getPeak = async () => {
        try {
            
            const response = await fetch("http://localhost:5000/peak")
            const jsonData = await response.json()

            setPeak(jsonData);
        } catch (err) {
