import React, {Fragment} from "react";

const EditElv = ({peak}) => {

  const [peak_elv, setElv] = useState("peak.peak_elv");

  const onSubmitForm = async(e) => {
      e.preventDefault();

      try {
          const body = {peak_elv};
          const response = await fetch(`http://localhost:5000/peaks/${peak.peak_id}`,{
              method: "PUT",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify(body)
          });
          window.location = "/";
      } catch (err) {
          console.error(err.message);
      };
  };

  return <Fragment>
    <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target={`#id${peak.peak_id}`}>
      Edit
    </button>

    <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div class="modal-dialog">
        <div class="modal-content">
          <div class="modal-header">
            <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div class="modal-body">
            ...
          </div>
          <div class="modal-footer">
            <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
            <button type="button" class="btn btn-primary">Save changes</button>
          </div>
        </div>
      </div>
    </div>
  </Fragment>;
};

export default EditElv;
