// import e from "express";
import React, {Fragment, useState} from "react";

const NewPeak = () => {

    const [peak_name, peak_elv, setPeak] = useState("");
    
    const onSubmitForm = async(e) => {
        e.preventDefault();
        try {
            const body_name = {peak_name};
            const body_elv = {peak_elv};
            const response = await fetch("http://localhost:5000/peak", {
                method: "Post",
                headers: {"Content-Type": "application/json"},
                body_name: JSON.stringify(body_name)
            });
            window.location = "/";
        } catch (err) {
            console.error(err.message);
        }
    }

    return (
        <Fragment>
            <h1 className="text-center mt-5 text-white">Peak Tracker</h1>
            <form className= "d-flex mt-5" onSubmit={onSubmitForm}>
                <input 
                    type = "text" 
                    className="form-control" 
                    value={peak_name} 
                    onChange={e => setPeak(e.target.value)} //targets the input and gets resulting value
                />
                <input 
                    type = "text" 
                    className="form-control" 
                    value={peak_elv} 
                    onChange={e => setPeak(e.target.value)} //targets the input and gets resulting value
                />
                <button className = "btn btn-success">Add</button>
            </form>
        </Fragment>
    )
}

export default NewPeak;
